package com.sqisoft.ssbr.datasync.entitybc;import com.mitzz.frame.exception.BizException;import com.mitzz.frame.exception.FKException;import com.mitzz.frame.exception.LException;import com.mitzz.frame.msg.MessageFactory;import com.mitzz.frame.vo.ValueObject;import com.sqisoft.ssbr.datasync.entitybc.dao.POSTUSERDAO;import com.sqisoft.ssbr.datasync.vo.POSTUSERVO;public class IDataSyncPostUser {		public POSTUSERVO retrievePostUserList(POSTUSERVO vo) throws LException {		POSTUSERDAO dao = new POSTUSERDAO();		return dao.retrievePostUserList(vo);	}	public int[] updatePostUserList(POSTUSERVO vo) throws LException {		POSTUSERDAO dao = new POSTUSERDAO();		try {			return dao.updatePostUserList(vo);		}		catch (FKException fe) {			throw new BizException(MessageFactory.getInstance().getMsg("EAA0001"),fe);		}	}		public int[] updateAprvUserList(ValueObject vo) throws LException {		POSTUSERDAO dao = new POSTUSERDAO();		try {			return dao.updateAprvUserList(vo);		}		catch (FKException fe) {			throw new BizException(MessageFactory.getInstance().getMsg("EAA0001"),fe);		}	}}