package com.sqisoft.ssbr.datasync.entitybc;import com.mitzz.frame.exception.BizException;import com.mitzz.frame.exception.FKException;import com.mitzz.frame.exception.LException;import com.mitzz.frame.msg.MessageFactory;import com.mitzz.frame.vo.ValueObject;import com.sqisoft.ssbr.datasync.entitybc.dao.USERDAO;import com.sqisoft.ssbr.datasync.vo.USERVO;public class IDataSyncUser {	public USERVO retrieveUserList(USERVO vo) throws LException {		USERDAO dao = new USERDAO();		return dao.retrieveUserList(vo);	}	public int[] updateUserList(USERVO vo) throws LException {		USERDAO dao = new USERDAO();		try {			return dao.updateUserList(vo);		}		catch (FKException fe) {			throw new BizException(MessageFactory.getInstance().getMsg("EAA0001"),fe);		}	}		public int[] updateUserCsvList(ValueObject vo) throws LException {		USERDAO dao = new USERDAO();		try {			return dao.updateUserCsvList(vo);		}		catch (FKException fe) {			throw new BizException(MessageFactory.getInstance().getMsg("EAA0001"),fe);		}	}}